query:
mutation SendDelegationSigned($input: SendDelegationInput!, $signature: SignatureInput!) {
  sendDelegation(input: $input, signature: $signature) {
    delegation {
      id
      hash
      kind
      nonce
      from
      to
      fee
      memo
      failureReason
      ... on UserCommandDelegation {
        delegator { publicKey }
        delegatee { publicKey }
      }
    }
  }
}
